import * as React from 'react';

import {Map,Marker,Popup} from 'react-map-gl';
import {useState} from 'react';
import {Room,Star} from '@material-ui/icons';
import './App.css';
import {useEffect} from 'react';
const axios=require('axios');
function App() {
    const[Pins,setPins] =useState([]);
    const[currentPlace,setCurrentPlace]=useState(null);
    const [viewState, setViewState] = useState({
        longitude: -100,
        latitude: 40,
        zoom: 2
      });
    useEffect(() => {
       const funct= async ()=>{
         try{
           const result=await axios.get("http://localhost:8800/api/pins");
           console.log(result.data);
            setPins(result.data);
         }
         catch(err)
         {console.log("nodes")}
       }
       funct();

    },[])
    const handleClick=(id)=>{
          setCurrentPlace(id);
    }
  return <Map
   {...viewState}
    style={{width: '100vw', height: '100vh'}}
    mapStyle="mapbox://styles/mapbox/streets-v9"
    onMove={evt => setViewState(evt.viewState)}
    onViewportChange={nextViewport=>{setViewState(nextViewport)}}
    mapboxAccessToken='pk.eyJ1IjoicmFqZ3VwdGE3NyIsImEiOiJjbDJxYjF6c2owYW43M2lwYmd2amwyemZvIn0.0IBA2qj2Q4RYrMhCLJu6Fw'
  >
       {Pins.map(p =>(

         <>
       <Marker latitude={p.latitude} longitude={p.longitude} >
      <Room style={{fontSize:viewState.zoom*10}} onClick={()=>{handleClick(p._id)}}/>
        </Marker>

        {p._id===currentPlace && 
        <Popup latitude={p.latitude} longitude={p.longitude} 
        anchor="left" onClose={setCurrentPlace(null)}>
        <div className='container'>
         <label>Title:</label>
         <h3 className='title'>{p.title}</h3>
         <label>Description:</label>
         <p className='descrip'>{p.desc}</p>
         <label>Rating:</label>
         <div className='stars'>
           <Star className='star'/>
           <Star className='star'/>
           <Star className='star'/>
           <Star className='star'/>
           <Star className='star'/>
         </div>
         <div>Marked br {p.userName}</div>
        </div>
         </Popup>
         }  
         </>

 ))} 
   </Map>;
}
export default App;
